  .body {
      font-family: Calibri;  
      font-size: 11px;
      margin: 0;
      padding: 0;
      }
  .page {
      page-break-after: auto;
      width: 100%;
      }
  @font-face {
      font-family: 'Calibri';
      src: url('/usr/share/fonts/calibri/calibri.ttf') format('truetype');
      font-weight: normal;
      font-style: normal;
  }
  .header-table {
      width: 100%;
      border: none;
      direction: rtl;
      border-collapse: collapse;
      margin-bottom: 10px;
      border-spacing:0;
      font-family: Calibri;
      }
  .header-table td, .header-table th {
      padding: 0;
      border: 1px solid #ddd;
      font-family: Calibri;  
      border-collapse: collapse;
      }
  .header-table .invoice-title {
      width: 45%;
      font-size: 20px;
      text-align: center;
      font-family: Calibri;
      }
  .header-table .original {
      font-size: 16px;
      text-align: center;
      width: 45%;
      font-family: Calibri;
      }
  .header-table .invoice-details {
      text-align: center;
      font-family: Calibri;
      }
  .header-table td {
      border: none;
      font-family: Calibri;
      }
  .info-table {
      width: 100%;
      font-family: Calibri;  
      margin-bottom: 20px;
      direction: rtl;
      border-collapse: collapse;
      border-spacing: 0;
      margin-top: 10px;
      }
  
  .info-table tr {
      padding: 0 0 0 0;
      border-spacing: 0;
      margin:0;
      font-family: Calibri;
      }
  
  .info-table td {
      border: none;
      vertical-align: middle;
      padding: 0 0 0 0;
      margin: 0;
      font-family: Calibri;
      }
  .info-table .info-1 {
      width: 10%;
      font-size: 11px;
      text-align: right;
      padding: 0;
      margin: 0;
      font-family: Calibri;
      }
  .info-table .info-2 {
      width: 21%;
      font-size: 11px;
      text-align: center;
      padding: 0;
      margin: 0;
      font-family: Calibri;
      }
  .info-table .info-3 {
      width: 14%;
      font-size: 11px;
      text-align: left;
      padding: 0;
      margin: 0;
      direction: ltr;
      font-family: Calibri;
      }
  .info-table .info-4 {
      width: 10%;
      padding: 0;
      margin: 0;
      font-family: Calibri;
      }
  .info-table .info-5 {
      width: 28%;
      font-size: 11px;
      text-align: left;
      padding: 0;
      margin: 0;
      vertical-align: middle!important;
      font-family: Calibri;
      }
  .info-table .info-6 {
      width: 17%;
      font-size: 11px;
      text-align: left;
      padding: 0;
      margin: 0;
      direction: ltr;
      font-family: Calibri;
      }
  hr.custom-hr2 {
      border: none;
      height: 1px;
      background-color: #000;
      font-family: Calibri;
      }
  .invoice-item {
      border-collapse: collapse;
      width: 100%;
      font-family: Calibri;
      margin-bottom: 20px;
      direction: rtl;
      table-layout: fixed;
      font-size: 11px;
      }
          /*.invoice-item th, .invoice-item td {
          border: 1px solid #ddd;
          }*/
  .invoice-item .col-1 {
      width: 15%;
      vertical-align: middle!important;
      font-family: Calibri;
      }
  .invoice-item .col-2 {
      width: 12%;
      vertical-align: middle!important;
      font-family: Calibri;
      }
  .invoice-item .col-3 {
      width: 15%;
      vertical-align: middle!important;
      font-family: Calibri;
      }
  .invoice-item .col-4 {
      width: 8%;
      vertical-align: middle!important;
      font-family: Calibri;
      }
  .invoice-item .col-5 {
      width: 8%;
      vertical-align: middle!important;
      font-family: Calibri;
      }
  .invoice-item .col-6 {
      width: 10%;
      vertical-align: middle!important;
      font-family: Calibri;
      }
  .invoice-item .col-7 {
      width: 32%;
      vertical-align: middle!important;
      font-family: Calibri;
      }
  hr.custom-hr {
      border: none;
      height: 2px;
      background-color: #00008B;
      font-family: Calibri;
      }
  .total {
      border-collapse: collapse!important;
      padding: 0!important;
      border-spacing: 0!important;
      margin: 0!important;
      width: 45%;
      font-size: 11px;
      font-family: Calibri;
      margin-bottom: 20px;
      direction: rtl;
      table-layout: fixed;
      float: right;
      }
  .total td, .total th {
      border: none;
      padding: 0!important;
      font-family: Calibri;
      }
  .total td {
      border-collapse: collapse!important;
      vertical-align: middle;
      text-align: right;
      border: none;
      font-family: Calibri;
      }
  .total tr {
      padding: 0!important; 
      border-spacing: 0!important;
      margin: 0!important;
      font-family: Calibri;
      }
  .total .c-1 {
      width: 10%;
      vertical-align: middle!important;
      font-family: Calibri;
      }
  .total .c-2 {
      width: 50%;
      vertical-align: middle!important;
      font-family: Calibri;
      }
  .total .c-3 {
      width: 40%;
      vertical-align: middle!important;
      direction: ltr;
      font-family: Calibri;
      }
  .installment {
      border-collapse: collapse;
      width: 45%;
      font-family: Calibri;
      margin-bottom: 20px;
      direction: rtl;
      table-layout: fixed;
      float: left;
      font-size: 11px;
      }
  .installment td {
      text-align: left;
      direction: ltr;
      font-family: Calibri;
      }
  .installment td, .installment th {
      border: none;
      padding: 0!important;
      font-family: Calibri;
      }
  .installment .installment-tr {
          height: 32px;
          font-family: Calibri;
      }

